def array_element_count
  puts ("Combien y a-t-il de journalistes dans ce array ?")
  print ("> ")
  puts (array.count)
  puts ("")
end

def array_with_number_count
  puts ("Combien d'handle contiennent un numéro ?")
  print ("> ")
  puts ((array.select { |x| x.each_char.any?("0".."9")}).count)
  puts ("")
end

def array_with_aude_count
  puts ("Combien d'handle contiennent les 4 lettres du prénom aude à la suite (sans prendre en compte les majuscules) ?")
  print ("> ")
  puts ((array.select { |x| x.downcase.include?("aude")}).count)
  puts ("")
end

def array_start_with_maj_count
  puts ("Combien commencent par une majuscule (première lettre juste après le @) ?")
  print ("> ")
  puts ((array.select { |x| x.delete_prefix("@").chr.each_char.any?("A".."Z")}).count)
  puts ("")
end

def array_with_maj_count
  puts ("Combien contiennent au moins une majuscule ?")
  print ("> ")
  puts ((array.select { |x| x.each_char.any?("A".."Z")}).count)
  puts ("")
end

def array_underscore_count
  puts ("Combien y a-t-il de underscore _ dans tous les pseudos confondus ?")
  print ("> ")
  n = 0
  array.each {|x| n += x.count '_'}
  puts n
  puts ("")
end

def array_sort
  puts ("Tri par ordre alphabétique :")
  puts (array.sort_by {|x| x.downcase})
  puts ("")
end

def array_50_shorters
  puts ("Les 50 handles les plus courts de ce array :")
  puts (array.sort_by(&:length).select.first(50))
  puts ("")
end

def array_find_epenser
  puts ("Quelle est la position dans l'array de la personne @epenser ?")
  print ("> ")
  puts ("@epenser se trouver à la position : #{array.index('@epenser')}")
end

def perform
  array
  array_element_count
  array_with_number_count
  array_with_aude_count
  array_start_with_maj_count
  array_with_maj_count
  array_underscore_count
  array_sort
  array_50_shorters
  array_find_epenser
end

